import React, { useEffect } from "react";
import { Link, useHistory } from "react-router-dom";

const NavBar = () => {
  const history = useHistory();
  useEffect(() => {
    const loggedInUser = localStorage.getItem("user");
  }, []);

  const logout = () => {
    localStorage.clear();
    window.location.reload();
  };

  return (
    <div>
<nav className="navbar navbar-light" style={{backgroundColor: "#737373"}}>
  <div id="navbarSupportedContent">
        <Link to={"/"} className="navbar-brand font-weight-bold text-light shadow-sm p-3 mb-5 rounded" style={{backgroundColor: "#FFA500"}}>
          Home
        </Link>
        {localStorage.getItem("authenticatedUser") != null && (
          <Link to={"/anime/new"} className="navbar-brand font-weight-bold shadow-sm p-3 mb-5 rounded" style={{backgroundColor: "#FFA500"}}>
            New Anime
          </Link>
        )}
        {localStorage.getItem("authenticatedUser") != null && (
          <Link to={"/genre/new"} className="navbar-brand font-weight-bold text-light shadow-sm p-3 mb-5 rounded" style={{backgroundColor: "#FFA500"}}>
            New Genre
          </Link>
        )}
        <Link to={"/anime/"} className="navbar-brand font-weight-bold text-light shadow-sm p-3 mb-5 rounded" style={{backgroundColor: "#FFA500"}}>
          All Anime
        </Link>
        {localStorage.getItem("authenticatedUser") == null && (
          <Link to={"/register/"} className="navbar-brand font-weight-bold text-light shadow-sm p-3 mb-5 rounded" style={{backgroundColor: "#FFA500"}}>
            Register
          </Link>
        )}
        {localStorage.getItem("authenticatedUser") == null && (
          <Link to={"/login/"} className="navbar-brand font-weight-bold text-light shadow-sm p-3 mb-5 rounded" style={{backgroundColor: "#FFA500"}}>
            Login
          </Link>
        )}
        {localStorage.getItem("authenticatedUser") != null && (
          <Link to={"/"} className="navbar-brand font-weight-bold text-light shadow-sm p-3 mb-5 rounded" style={{backgroundColor: "#FFA500"}} onClick={logout}>
            Logout
          </Link>
        )}
              </div>
</nav>
</div>


  );
};

export default NavBar;
