import React, { useEffect, useContext } from "react";
import AnimeFinder from "../apis/AnimeFinder";
import { AnimeContext } from "../context/AnimeContext";
import { useHistory } from "react-router-dom";

const AnimeList = (props) => {
  const { anime, setAnime } = useContext(AnimeContext);
  let history = useHistory();
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await AnimeFinder.get("/");
        console.log(response.data.data);
        setAnime(response.data.data.anime);
      } catch (err) {}
    };

    fetchData();
  }, []);

  return (
    <div className="list-group">
        Anime List Component
        <table class="table table-hover table-dark table-sm">
        <thead>
          <tr className="bg-primary">
            <th scope="col">Image</th>
            <th scope="col">Name</th>
            <th scope="col">Number of Episodes</th>
            <th scope="col">Year</th>
            <th scope="col">Creator</th>
            <th scope="col">Genre</th>
          </tr>
        </thead>
        <tbody>
          {anime &&
            anime.map((anime) => {
              return (
                <tr
                  key={anime.id}
                  class="table-active"
                >
                  <td><img src={anime.image} class="img-fluid rounded w-25 mh-100"></img></td>
                  <td class="col align-self-center">{anime.name}</td>
                  <td>{anime.episodes}</td>
                  <td>{anime.year}</td>
                  <td>{anime.creator}</td>
                  <td> {anime.genre_name}</td>
                </tr>
              );
            })}
        </tbody>
        </table>
    </div>
  );
};

export default AnimeList;
