import React, { useState, useContext } from "react";
// import { postUserAPICall } from "../apis/AnimeFinder";
import { UserContext } from "../context/UserContext";

const Register = () => {
  // const { addUsers } = useContext(UserContext);
  const [first, setFirst] = useState("");
  const [last, setLast] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  // const handleSubmit = async (e) => {
  //   //   e.preventDefault();
  //   //   try {
  //   //     const response = await postGenreAPICall({
  //   //       genre_name: name,
  //   //       genre_image:image,
  //   //       genre_description: description,
  //   //     });
  //   //     console.log(response.data.data);
  //   //     console.log(response.status);
  //   //     addGenres(response.data.data.genres);
  //   //     if(response.status === 201) {
  //   //       history.push("/")
  //   //     }
  //   //   } catch (err) {
  //   //     console.log(err);
  //   //     <Redirect to="/error" />
  //   //   }
  // };

  return (
    <div className="container-fluid">
      <h3 className="font-weight-light text-left">Registration Page</h3>

      <div className="row">
        <div className="col-md-12">
          <form role="form">
            <div className="form-group">
              <label htmlFor="first">First Name</label>
              <input
                type="text"
                value={first}
                onChange={(e) => setFirst(e.target.value)}
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label htmlFor="last">Last Name</label>
              <input
                type="text"
                value={last}
                onChange={(e) => setLast(e.target.value)}
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label htmlFor="creator">UserName</label>
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label htmlFor="creator">Password</label>
              <input
                type="text"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="form-control"
              />
            </div>
            <button
              // onClick={handleSubmit}
              type="submit"
              className="btn btn-primary"
            >
              Create User
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Register;
