import React, { useEffect, useContext } from "react";
import { useHistory } from "react-router-dom";
// import RestaurantFinder from "../apis/RestaurantFinder";
// import { RestaurantsContext } from "../context/RestaurantsContext";

const GenreList = (props) => {
  return (
    <div class="list-group container">
        Genre List Component
        <table class="table-active table-bordered table-striped table-hover table-dark table-sm">
        <thead>
          <tr className="bg-secondary text-warning">
            <th scope="col" class="col-md-3-3 text-center border-light">Name</th>
          </tr>
        </thead>
        <tbody>
          {genre &&
            genre.map((genre) => {
              return (
                <tr
                  key={genre.id}
                  class="table-active"
                >
                  <td class="col-md-3 text-center border-light"><img src={genre.name} class="img-fluid rounded h-25 w-75 p-1 border border-warning bg-warning"></img></td>
                </tr>
              );
            })}
        </tbody>
        </table>
    </div>
  );
};

export default GenreList;
