import React, { useEffect, useContext, useState } from "react";
import { getAnimeInGenre } from "../apis/AnimeFinder";
import { AnimeContext } from "../context/AnimeContext";
import { useHistory, useParams, Redirect, Link } from "react-router-dom";
import MyLoader from "./MyLoader";

const SearchBar = (props) => {
  const [search, setSearch] = useState("");
  let history = useHistory();
  const REQUEST_STATUS = {
    LOADING: "loading",
    SUCCESS: "success",
    FAILURE: "failure",
  };
  const [requestStatus, setRequestStatus] = useState(REQUEST_STATUS.LOADING);

  useEffect((e) => {
    const fetchData = async () => {
      setSearch(e.target.value);
    };
    fetchData();
  }, []);

  // if (requestStatus === REQUEST_STATUS.LOADING) return <MyLoader></MyLoader>;
  // if (requestStatus === REQUEST_STATUS.FAILURE) <Redirect to="/error" />;

  const handleSearch = (e) => {
    setSearch(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
      <Redirect to={`/anime/search/${search}`} />;
    };



  return (
    <div className="">
<div className="input-group rounded w-50s">
  <input type="search" className="form-control rounded" onClick={handleSubmit} onChange={handleSearch} placeholder="Search" aria-label="Search" aria-describedby="search-addon" />
  <span className="input-group-text border-0 navLink" id="search-addon">
  <Link to={`/anime/search/${search}`}><i className="fas fa-search"></i></Link>
  </span>
  <span>
    <h1>{search}</h1>
    </span>
</div>
</div>
  );
};

export default SearchBar;
