import React, { useEffect, useContext } from "react";
import { getGenreCall } from "../apis/AnimeFinder";
import { AnimeContext } from "../context/AnimeContext";
import { GenresContext } from "../context/GenreContext";
import { useHistory } from "react-router-dom";

const GenreList = (props) => {
  const { genres, setGenres } = useContext(GenresContext);
  let history = useHistory();
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await getGenreCall();
        console.log(response.data.data);
        setGenres(response.data.data.genre);
      } catch (err) {}
    };
    fetchData();
  }, []);

  return (
    <div class="list-group container">
        Genre List Component
        <table class="table-active table-bordered table-striped table-hover table-dark table-sm">
        <thead>
          <tr className="bg-secondary text-warning">
          <th scope="col" class="col-md-6-6 text-center border-light">Name</th>
            <th scope="col" class="col-md-6-6 text-center border-light">Genre Image</th>
          </tr>
        </thead>
        <tbody>
          {genres &&
            genres.map((genre) => {
              return (
                <tr
                  key={genre.id}
                  class="table-active"
                >
                  <td class="col-md-4-6 text-center border-light">{genre.genre_name}</td>
                  <td class="col-md-8-4 text-center border-light"><img src={genre.genre_image} class="img-fluid rounded p-1 border border-warning bg-warning"></img></td>
                </tr>
              );
            })}
        </tbody>
        </table>
    </div>
  );
};

export default GenreList;
