import React, { useEffect, useContext, useState } from "react";
import { getAnimeInGenre } from "../apis/AnimeFinder";
import { AnimeContext } from "../context/AnimeContext";
import { useHistory, useParams, Redirect, Link } from "react-router-dom";
import MyLoader from "./MyLoader";

const SearchBar = (props) => {
  const [search, setSearch] = useState("");
  let history = useHistory();

  const handleSearch = (e) => {
    setSearch(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    window.location.href=(`/anime/search/${search}`);
      <Redirect to={`/anime/search/${search}`} />;
    };

    const onKeyDownHandler = (e) => {
      if (e.keyCode === 13) {
        handleSubmit();
      }
    };



  return (
    <div className="">
<div className="input-group rounded w-50s">
  <input type="search" className="form-control rounded" onChange={handleSearch} onKeyUp={onKeyDownHandler} placeholder="Search" aria-label="Search" aria-describedby="search-addon" />
  <span className="input-group-text border-0 navLink" id="search-addon">
  <i className="fas fa-search" onClick={handleSubmit}><Link to={`/anime/search/${search}`}></Link></i>
  </span>
</div>
</div>
  );
};

export default SearchBar;
