import React, { useEffect, useContext } from "react";
import AnimeFinder from "../apis/AnimeFinder";
import { AnimeContext } from "../context/AnimeContext";
import { GenreContext } from "../context/GenreContext";
import { useHistory } from "react-router-dom";

const GenreList = (props) => {
  const { genre, setGenre } = useContext(GenreContext);
  let history = useHistory();
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await AnimeFinder.get("/genre");
        console.log(response.data.data);
        setGenre(response.data.data.genre);
      } catch (err) {}
    };
    fetchData();
  }, []);

  return (
    <div class="list-group container">
        Genre List Component
        <table class="table-active table-bordered table-striped table-hover table-dark table-sm">
        <thead>
          <tr className="bg-secondary text-warning">
            <th scope="col" class="col-md-3-3 text-center border-light">Name</th>
          </tr>
        </thead>
        <tbody>
          {genre &&
            genre.map((genre) => {
              return (
                <tr
                  key={genre.id}
                  class="table-active"
                >
                  <td class="col-md-3 text-center border-light">{genre.genre_name}</td>
                </tr>
              );
            })}
        </tbody>
        </table>
    </div>
  );
};

export default GenreList;
